import { CartModal } from "../Cartmodal";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { useHistory } from 'react-router-dom';
import { useEffect } from 'react';

export const showModal = function (
  currentModalState,
  updateModalState,
  cartItems
) {

  const history = useHistory(); // Add this line to use the useHistory hook

  useEffect(() => {
    if (!currentModalState) {
      history.push('/checkout');
    }
  }, [currentModalState, history]);



  if (currentModalState) {
    return (
      <div>
        {" "}
        <CartModal
          open={currentModalState}
          onDismiss={() => updateModalState()}
          title="Order"
          content="There are no items in your cart"
          actions={
            <div className="ui teal button">
              {" "}
              <Link
                to="/checkout"
                className="menuitem"
                onClick={() => {
                  updateModalState();
                }}
              >
                <span style={{ color: "#ffffff" }}> Check Out</span>
              </Link>
            </div>
          }
          items={cartItems}
        />
      </div>
    );
  }


};
